			<principalType>user</principalType>
			<readPrivilege>allow</readPrivilege>
			<tracked>1</tracked>
		  </aclEntry>
		</object>
		<object>
		  <objectId>systemObject-f9e15a45-04b4-11e8-9381-4ec220524153</objectId>
		  <objectName>directory</objectName>
		  <objectType>systemObject</objectType>
		  <aclEntry>
			<aclEntryId>063fb45b-04b5-11e8-943f-4ec220524153</aclEntryId>
			<changePermissionsPrivilege>inherit</changePermissionsPrivilege>
			<executePrivilege>inherit</executePrivilege>
			<modifyPrivilege>allow</modifyPrivilege>
			<principalName>project: EC-Utilities</principalName>
			<principalType>user</principalType>
			<readPrivilege>allow</readPrivilege>
			<tracked>1</tracked>
