sub getJobStepsDurationInfo {
   my $ec = shift; 
   my $jobID = shift;
   my $jobStepName = shift; 
   my $result = "";

   my ($success, $res, $errMsg, $errCode)= invokeCommand($ec, "SuppressLog IgnoreError", "getJobDetails", $jobID); 
   if ($success) {
        my $jobName = $res->findnodes("//job/jobName")->string_value();
        my $jobStart = $res->findnodes("//job/start")->string_value();


        my $jobSteps = $res->findnodes("//job/jobStep");
        foreach my $stepNode ($jobSteps->get_nodelist) {
            my $stepName = $stepNode->findnodes("stepName")->string_value();
            if($stepName ne "$jobStepName") {
                next;
            }

            my $outcome = $stepNode->findnodes("outcome")->string_value();
            if($outcome eq "error") {
                next;
            }

            my $elapsedTime = $stepNode->findnodes("elapsedTime")->string_value();
            if($elapsedTime ne "") {
                my $seconds=($elapsedTime/1000)%60;
                my $minutes=($elapsedTime/(1000*60))%60;
                my $hours=($elapsedTime/(1000*60*60))%24;
                $result =  "https://sbas.mobis.co.kr/commander/link/jobDetails/jobs/$jobID , JOB_START=> $jobStart, BUILD_ELAPSEDTIME=> $hours:$minutes:$seconds\n";
                return $result;
            }
       }
   }
}

property.description:
sub getJobStepsOutcome {
   my $ec = shift; 
   my $jobStepName = '$[/myJobStep/stepName]';
   my $result = '';

   my ($success, $res, $errMsg, $errCode)= invokeCommand($ec, "SuppressLog IgnoreError", "getJobDetails", $ENV{COMMANDER_JOBID}); 
   if (! $success) {
       print(" Error getting job detail information.\n");
       print(" $errCode: $errMsg\n");

   } else {
		print $res->findnodes_as_string('/') . "\n";

		#my $abortState = $res->findnodes("//job/abortStatus")->string_value();
		#if(defined $abortState && $abortState eq 'ABORT') {
		#	return 'abort';
		#}

       my $jobSteps = $res->findnodes("//job/jobStep");
       foreach my $stepNode ($jobSteps->get_nodelist) {
            my $stepName = $stepNode->findnodes("stepName")->string_value();
            if($stepName ne $jobStepName) {
               my $outcome = $stepNode->findnodes("outcome")->string_value();
               if($outcome eq "error") {
                   return "error";    
               } else {
                   $result = "success";
               }

            }
       }
   }

   return $result;
}

