sub createSelectParam{

    my ($ec, $paramName, $projectName, $procedureName, $description, $required, $defaultValue, $options) = @_;

    if($defaultValue ne '') {
        my($success, $xPath, $errMsg, $errCode) = invokeCommand($ec," SuppressLog IgnoreError " , 'createFormalParameter' ,
                        $projectName, $procedureName, $paramName, 
                         {'description' => $description,
                          'type' => 'select',
                          'required' => $required,
                          'defaultValue' => $defaultValue
                         }); 
		if (! $success) {
                printf(" Error createSelectParam - %s\n", $paramName);
                printf(" %s: %s\n", $errCode, $errMsg);
                return 0;
        }

    } else {
        my($success, $xPath, $errMsg, $errCode) = invokeCommand($ec," SuppressLog IgnoreError " , 'createFormalParameter' ,
                        $projectName, $procedureName, $paramName, 
                         {'description' => $description,
                          'type' => 'select',
                          'required' => $required
                         });
		if (! $success) {
                printf(" Error createSelectParam - %s\n", $paramName);
                printf(" %s: %s\n", $errCode, $errMsg);
                return 0;
        }
    }



	unless(defined $options) {
		$ec->setProperty("/projects/$projectName/procedures/$procedureName/ec_customEditorData/parameters/$paramName/formType" , "standard"); 
		$ec->setProperty("/projects/$projectName/procedures/$procedureName/ec_customEditorData/parameters/$paramName/options/optionCount" , 0);
		$ec->setProperty("/projects/$projectName/procedures/$procedureName/ec_customEditorData/parameters/$paramName/options/type" , 'list');
		return 1;
	}

	my $counter = 1;
    my @keys = sort keys %$options;
    foreach my $key ( @keys ) {
        #print "option : $option \n";
        $ec->setProperty( "/projects/$projectName/procedures/$procedureName/ec_customEditorData/parameters/$paramName/options/option" . $counter . "/text" ,  $key);
        $ec->setProperty( "/projects/$projectName/procedures/$procedureName/ec_customEditorData/parameters/$paramName/options/option" . $counter . "/value" , $options->{"$key"} );
        $counter ++;
    }

    $ec->setProperty("/projects/$projectName/procedures/$procedureName/ec_customEditorData/parameters/$paramName/formType" , "standard"); 
    $ec->setProperty("/projects/$projectName/procedures/$procedureName/ec_customEditorData/parameters/$paramName/options/optionCount" , $counter - 1);
    $ec->setProperty("/projects/$projectName/procedures/$procedureName/ec_customEditorData/parameters/$paramName/options/type" , 'list');
    return 1;
 }

sub createSelectParamWithPropertySheet{
    my ($ec, $paramName, $projectName, $propertySheetProj, $procedureName, $description, $required, $defaultValue, $options) = @_;

    if($defaultValue ne '') {
        my($success, $xPath, $errMsg, $errCode) = invokeCommand($ec," SuppressLog IgnoreError " , 'createFormalParameter' ,
                        $projectName, $procedureName, $paramName, 
                         {'description' => $description,
                          'type' => 'select',
                          'required' => $required,
                          'defaultValue' => $defaultValue
                         }); 
		if (! $success) {
                printf(" Error createSelectParamWithPropertySheet - %s\n", $paramName);
                printf(" %s: %s\n", $errCode, $errMsg);
                return 0;
        }

    } else {
        my($success, $xPath, $errMsg, $errCode) = invokeCommand($ec," SuppressLog IgnoreError " , 'createFormalParameter' ,
                        $projectName, $procedureName, $paramName, 
                         {'description' => $description,
                          'type' => 'select',
                          'required' => $required
                         }); 
		if (! $success) {
                printf(" Error createSelectParamWithPropertySheet - %s\n", $paramName);
                printf(" %s: %s\n", $errCode, $errMsg);
                return 0;
        }
    }

    my $counter = 1;

    @keys = sort keys %$options;
    foreach my $key ( @keys ) {
        #print "option : $option \n";
        $ec->setProperty( "/projects/$propertySheetProj/parameters/$paramName/option" . $counter . "/text" , $key );
        $ec->setProperty( "/projects/$propertySheetProj/parameters/$paramName/option" . $counter . "/value" , $options->{"$key"} );
        $counter ++;
    }

	$ec->setProperty("/projects/$propertySheetProj/parameters/$paramName/optionCount" , $counter - 1);
    $ec->setProperty("/projects/$projectName/procedures/$procedureName/ec_customEditorData/parameters/$paramName/formType" , "standard"); 
    $ec->setProperty("/projects/$projectName/procedures/$procedureName/ec_customEditorData/parameters/$paramName/options/propertyPath" , "/projects/$propertySheetProj/parameters/$paramName");
    $ec->setProperty("/projects/$projectName/procedures/$procedureName/ec_customEditorData/parameters/$paramName/options/type" , 'entry');
	return 1;
 }

