    my $counter = 1;

    my @keys = sort keys %$options;
    foreach my $key ( @keys ) {
        #print "option : $option \n";
        $ec->setProperty( "/projects//$propertySheetProj/sharedParam/$projectName/parameters/$paramName/option" . $counter . "/text" , $key );
