sub deleteFormalParam{
    my ($ec, $paramName, $projectName, $procedureName) = @_;

    my($success, $xPath, $errMsg, $errCode) = invokeCommand($ec," SuppressLog IgnoreError " , 'getFormalParameters' ,
                                                             {
                                                             'projectName' => $projectName,
                                                             'procedureName' => $procedureName 
                                                             });

    if (! $success) {
        printf(" Error deleteFormalParam - %s\n", $paramName);
        printf(" %s: %s\n", $errCode, $errMsg);
        return 0;
    }

    my @nodes = $xPath->findnodes("//formalParameter");
    foreach my $node (@nodes) { 
         my $parameterName = $node->findvalue("formalParameterName"); 
         if($parameterName eq $paramName){
            ($success, $xPath, $errMsg, $errCode) = invokeCommand($ec," SuppressLog IgnoreError " , 'deleteFormalParameter' ,
                                                                    { projectName => $projectName,
                                                                     procedureName=>$procedureName,
                                                                     formalParameterName=>$paramName,});
            if (! $success) {
                printf(" Error deleteFormalParam - %s\n", $paramName);
                printf(" %s: %s\n", $errCode, $errMsg);
                return 0;
            } else {
                $ec->deleteProperty("/projects/$projectName/procedures/$procedureName/ec_customEditorData/parameters/$paramName");
            } 
        } 
    }

    return 1;
}

sub getFormalParameterOptionList {
	my ($ec, $projectName, $procedureName) = @_;

	my $xPath = $ec->getFormalParameters( {'projectName' => $projectName, 'procedureName' => $procedureName});

	my @nodes = $xPath->findnodes("//formalParameter");

	my %options = ();
	my %optionLists = ();

	foreach my $node (@nodes) { 
		my $paramName = $node->findvalue("formalParameterName");
		my $paramType = $node->findvalue("type");
		my $defaultValue = $node->findvalue("defaultValue");
		my $required = $node->findvalue("required");

		unless (defined $paramName && $paramName ne '') {
			return undef;
		}

		if($paramType eq 'select' || $paramType eq 'radio') {
			my $optionListStr = getPropertyStringValue( $ec, "/projects/$projectName/procedures/$procedureName/ec_customEditorData/parameters/$paramName/options/list");

			if (defined $optionListStr && $optionListStr ne '') {
				my @selectListArray = split /\|/, $optionListStr;
				foreach (@selectListArray) {
					$selectLists{"$_"}="$_";
				}
			} else {
				my $optCnt = getPropertyValue( $ec, "/projects/$projectName/procedures/$procedureName/ec_customEditorData/parameters/$paramName/options/optionCount");

				unless (defined $optCnt && $optCnt > 0) {
					return undef;
				}

				for(my $i = 1; $i < $optCnt + 1; $i++) {
					my $optKey = getPropertyStringValue( $ec, "/projects/$projectName/procedures/$procedureName/ec_customEditorData/parameters/$paramName/options/option$i/text");
					unless (defined $optKey && $optKey ne "") {
						return undef;
					}

					my $optValue = getPropertyStringValue( $ec, "/projects/$projectName/procedures/$procedureName/ec_customEditorData/parameters/$paramName/options/option$i/value");
					unless (defined $optValue && $optValue ne "") {
						return undef;
					}

					$selectLists{"$optKey"}="$optValue";
				}
			}

			my $size = keys %selectLists;
			unless ($size > 0) {
				return undef;
			}

			$options{"type"} = "$paramType";
			$options{"required"} = "$required";
			$options{"defaultValue"}="$defaultValue";
			$options{"list"}= (\%selectLists);
			$optionLists{"$paramName"} = (\%options);

		} elsif($paramType eq 'entry') {
			$options{"type"} = "entry";
			$options{"required"} = "$required";
			$options{"defaultValue"}="$defaultValue";
			$optionLists{"$paramName"} = (\%options);

		} elsif($paramType eq 'checkbox') {
			$options{"type"} = "checkbox";
			$options{"required"} = "$required";
			$options{"defaultValue"}="$defaultValue";
			$optionLists{"$paramName"} = (\%options);

		} elsif($paramType eq 'textarea') {
			$options{"type"} = "textarea";
			$options{"required"} = "$required";
			$options{"defaultValue"}="$defaultValue";
			$optionLists{"$paramName"} = (\%options);
		}
	}

	return (\%optionLists);
}

sub getFormalParameterValues {
    my ($ec, $projectName, $procedureName) = @_;

    my $xPath = $ec->getFormalParameters( {'projectName' => $projectName, 'procedureName' => $procedureName});

    my @nodes = $xPath->findnodes("//formalParameter");

    my %options = ();

    foreach my $node (@nodes) {    
        my $paramName = $node->findvalue("formalParameterName");    
        my $paramValue = $ec->getProperty({propertyName => $paramName})->findvalue("//value");
        $options{"$paramName"} = "$paramValue";
    }

    return \%options;
}

sub getGeneralSelectParamOptions {
    my ($ec, $paramName, $projectName, $procedureName) = @_;

	unless (defined $paramName && $paramName ne '') {
        return undef;
    }

    my %selectLists = ();

    my $optionListStr = getPropertyStringValue( $ec, "/projects/$projectName/procedures/$procedureName/ec_customEditorData/parameters/$paramName/options/list");

    if (defined $optionListStr && $optionListStr ne '') {
        my @selectListArray = split /\|/, $optionListStr;
        foreach (@selectListArray) {
            $selectLists{"$_"}="$_";
        }
    } else {
        my $optCnt = getPropertyValue( $ec, "/projects/$projectName/procedures/$procedureName/ec_customEditorData/parameters/$paramName/options/optionCount");
        unless (defined $optCnt && $optCnt > 0) {
            return undef;
        }

        for(my $i = 1; $i < $optCnt + 1; $i++) {
            my $optKey = getPropertyStringValue( $ec, "/projects/$projectName/procedures/$procedureName/ec_customEditorData/parameters/$paramName/options/option$i/text");
            unless (defined $optKey && $optKey ne "") {
                return undef;
            }

            my $optValue = getPropertyStringValue( $ec, "/projects/$projectName/procedures/$procedureName/ec_customEditorData/parameters/$paramName/options/option$i/value");
            unless (defined $optValue && $optValue ne "") {
                return undef;
            }

            $selectLists{"$optKey"}="$optValue";
        }
    }

    my $size = keys %selectLists;
    unless ($size > 0) {
        return undef;
    }

    return (\%selectLists);
}

sub getProcedureFormalParamDefaultValue{
    my ($ec, $paramName, $projectName, $procedureName) = @_;

    my($success, $xPath, $errMsg, $errCode) = invokeCommand($ec," SuppressLog IgnoreError " , 'getFormalParameter' ,
                                                             {
                                                             'projectName' => $projectName,
                                                             'procedureName' =>$procedureName,
															 'formalParameterName' => $paramName
                                                             });

    if (! $success) {
        printf(" Error getProcedureFormalParamDefaultValue - %s\n", $paramName);
        printf(" %s: %s\n", $errCode, $errMsg);
        return undef;
    }

	return $xPath->findvalue("//defaultValue")->string_value();
}

sub getScheduleActualParameters {
    my ($ec, $projectName, $scheduleName) = @_;

    my($success, $xPath, $errMsg, $errCode)= invokeCommand($ec, "SuppressLog IgnoreError", "getActualParameters",
                                                         { 'projectName'=>"$projectName", 
                                                           'scheduleName'=>"$scheduleName"
                                                         });
    if (! $success) {
		 printf(" Error getScheduleActualParameters - scheduleName : %s\n", $workflowName);
		 printf(" %s: %s\n", $errCode, $errMsg);
		 return undef;
    }

    my %actualParameters = ();
    my @params = $xPath->findnodes("//actualParameter");
    foreach my $p (@params)
     {
         my $name = $p->findvalue("actualParameterName")->value;
         my $val = $p->findvalue("value")->value;
         $actualParameters{"$name"} = "$val";
     }

    return \%actualParameters;
}

sub getSharedSelectParamOptions {
    my ($ec, $paramName, $propertySheetProj, $projectName) = @_;

    unless (defined $paramName && $paramName ne '') {
        return undef;
    } 

    my %options = (); 

    my $optCnt = getPropertyValue( $ec, "/projects/$propertySheetProj/sharedParam/$projectName/parameters/$paramName/optionCount");
    unless (defined $optCnt && $optCnt > 0) {
        return undef;
    }

    for(my $i = 1; $i < $optCnt + 1; $i++) {

        my $optKey = getPropertyStringValue( $ec, "/projects/$propertySheetProj/sharedParam/$projectName/parameters/$paramName/option$i/text");
        unless (defined $optKey && $optKey ne "") {
            return undef;
        }

        my $optValue = getPropertyStringValue( $ec, "/projects/$propertySheetProj/sharedParam/$projectName/parameters/$paramName/option$i/value");
        unless (defined $optValue && $optValue ne "") {
            return undef;
        }

        $options{"$optKey"}="$optValue";
    } 

    my $size = keys %options;
    unless ($size > 0) {
        return undef;
    } 

    return (\%options);
}


sub getSpecifiedFormalParameterOptionList {
    my ($ec, $param, $projectName, $procedureName) = @_;

    my $xPath = $ec->getFormalParameters( {'projectName' => $projectName, 'procedureName' => $procedureName});

    my @nodes = $xPath->findnodes("//formalParameter");

    my %options = ();
    my %optionLists = ();

    foreach my $node (@nodes) { 
        my $paramName = $node->findvalue("formalParameterName");
		unless ($paramName eq $param) {
			next;
		} 

        my $paramType = $node->findvalue("type");
        my $defaultValue = $node->findvalue("defaultValue");
        my $required = $node->findvalue("required"); 

        unless (defined $paramName && $paramName ne '') {
            return undef;
        }

        if($paramType eq 'select' || $paramType eq 'radio') {
	    my %selectLists = ();
            my $optionListStr = getPropertyStringValue( $ec, "/projects/$projectName/procedures/$procedureName/ec_customEditorData/parameters/$paramName/options/list");

            if (defined $optionListStr && $optionListStr ne '') {
                my @selectListArray = split /\|/, $optionListStr;
                foreach (@selectListArray) {
                    $selectLists{"$_"}="$_";
                }
            } else {
                my $optCnt = getPropertyValue( $ec, "/projects/$projectName/procedures/$procedureName/ec_customEditorData/parameters/$paramName/options/optionCount");
                unless (defined $optCnt && $optCnt > 0) {
                    return undef;
                }

                for(my $i = 1; $i < $optCnt + 1; $i++) {
                    my $optKey = getPropertyStringValue( $ec, "/projects/$projectName/procedures/$procedureName/ec_customEditorData/parameters/$paramName/options/option$i/text");
                    unless (defined $optKey && $optKey ne "") {
                        return undef;
                    }

                    my $optValue = getPropertyStringValue( $ec, "/projects/$projectName/procedures/$procedureName/ec_customEditorData/parameters/$paramName/options/option$i/value");
                    unless (defined $optValue && $optValue ne "") {
                        return undef;
                    }

                    $selectLists{"$optKey"}="$optValue";
                }
            }

            my $size = keys %selectLists;
            unless ($size > 0) {
                return undef;
            }

            $options{"type"} = "$paramType";
            $options{"required"} = "$required";
            $options{"defaultValue"}="$defaultValue";
            $options{"list"}= (\%selectLists);
            $optionLists{"$paramName"} = (\%options);

        } elsif($paramType eq 'entry') {
            $options{"type"} = "entry";
            $options{"required"} = "$required";
            $options{"defaultValue"}="$defaultValue";
            $optionLists{"$paramName"} = (\%options);

        } elsif($paramType eq 'checkbox') {
            $options{"type"} = "checkbox";
            $options{"required"} = "$required";
            $options{"defaultValue"}="$defaultValue";
            $optionLists{"$paramName"} = (\%options);

        } elsif($paramType eq 'textarea') {
            $options{"type"} = "textarea";
            $options{"required"} = "$required";
            $options{"defaultValue"}="$defaultValue";
            $optionLists{"$paramName"} = (\%options);
        }
    }

    return (\%optionLists);
}

sub getWorkflowActualParameters {
	my ($ec, $projectName, $workflowName, $stateName) = @_;



	my($success, $xPath, $errMsg, $errCode)= invokeCommand($ec, "SuppressLog IgnoreError", "getActualParameters",
														 { 'projectName'=>"$projectName", 
														   'workflowName'=>"$workflowName",
														   'stateName'=>"$stateName"
														 });
	if (! $success) {
	  printf(" Error getWorkflowActualParameters - WorkflowName : %s, StateName : %s ", $workflowName, $stateName);
	  printf(" %s: %s\n", $errCode, $errMsg);
	  return undef;
	}

	my %actualParameters = ();
	my @params = $xPath->findnodes("//actualParameter");
	foreach my $p (@params)
	 {
		 my $name = $p->findvalue("actualParameterName")->value;
		 my $val  = $p->findvalue("value")->value;
		 $actualParameters{"$name"} = "$val";
	 }

	return \%actualParameters;
}

sub modifyFormalParamWorkflow{
    my ($ec, $paramName, $projectName, $workflowName, $stateName, $defaultValue, $description) = @_;

    my($success, $xPath, $errMsg, $errCode) = invokeCommand($ec," SuppressLog IgnoreError " , 'getFormalParameters' ,
                                                             {
                                                             'projectName' => $projectName,
															 'workflowName' =>$workflowName,
															 'stateName'=>$stateName
                                                             });

    if (! $success) {
        printf(" Error modifyFormalParam - %s\n", $paramName);
        printf(" %s: %s\n", $errCode, $errMsg);
        return 0;
    }

    my @nodes = $xPath->findnodes("//formalParameter");
    foreach my $node (@nodes) { 
         my $parameterName = $node->findvalue("formalParameterName"); 
         if($parameterName eq $paramName){
            ($success, $xPath, $errMsg, $errCode) = invokeCommand($ec," SuppressLog IgnoreError " , 'modifyFormalParameter' ,
                                                                    { 'projectName' => $projectName,
																	 'workflowName' =>$workflowName,
																	 'stateName'=>$stateName,
																	 'formalParameterName' =>$paramName,
																	 'defaultValue' => $defaultValue,
																	 'description' =>$description
																	 });
            if (! $success) {
                printf(" Error modifyFormalParam - %s\n", $paramName);
                printf(" %s: %s\n", $errCode, $errMsg);
                return 0;
            } 
        } 
    }

    return 1;
}

sub modifyGeneralSelectParam {
    my ($ec, $targetProject, $targetProcedure, $description, $paramName, $required, $paramDefault, $optionList) = @_;

	my $res = 1;
	if(checkFormalParamExist($ec,$paramName, $targetProject, $targetProcedure)) {
		my $optionBasePath = "/projects/$targetProject/procedures/$targetProcedure/ec_customEditorData/parameters"; 
		my $oldOptList = getGeneralSelectParamOptions($ec, $paramName, $targetProject, $targetProcedure);

		unless(defined $oldOptList) {
        print(" Error modifyGeneralSelectParam $paramName\n");
            print(" \tNo old option found\n");
            return 0;
        }

		print '[Old Option List] : '. Dumper $oldOptList;
		print "\n";

		my $oldDefaultValue = getProcedureFormalParamDefaultValue($ec,$paramName, $targetProject, $targetProcedure);
		my $counter = getPropertyValue( $ec, "$optionBasePath/$paramName/options/optionCount" ) + 1;

		my $newCounter = 0;
		my @keys = sort keys %$optionList;
		foreach my $key ( @keys ) {
			if (defined $oldOptList->{$key}) {
				next;
			}

			$res = setProperty( $ec, "$optionBasePath/$paramName/options/option".$counter."/text", $key );
			if($res == 0) {
                print(" Error modifyGeneralSelectParam $paramName\n");
                print(" \tFailed to set option key - $key\n");
                return 0;
            }

			my $value = $optionList->{"$key"};

			$res = setProperty( $ec, "$optionBasePath/$paramName/options/option".$counter."/value", $optionList->{"$key"} );
			if($res == 0) {
                print(" Error modifyGeneralSelectParam $paramName\n");
                print(" \tFailed to set option value - $value\n");
                return 0;
            }

			print "[AddedOption] : KEY => $key - VALUE => $value\n\n";
			$counter ++;
			$newCounter ++;
		}

		@keys = sort keys %$oldOptList;
		foreach my $key ( @keys ) {
			unless (defined $optionList->{$key}) {
				$optionList->{$key} = $oldOptList->{$key};
			}
		}

		my $hasValue = 0;
		@keys = sort keys %$optionList;
		foreach my $key ( @keys ) {
			if($paramDefault eq $optionList->{$key}) {
				$hasValue = 1;
				last;
			}
		}

                print '[New Option List] : '. Dumper $optionList;

		if( $newCounter > 0 ) {

			$res = setProperty($ec, "$optionBasePath/$paramName/options/optionCount", $counter -1);
            if($res == 0) {
                print(" Error modifyGeneralSelectParam $paramName\n");
                print(" \tFailed to set optionCount\n");
                return 0;
            }

			if($paramDefault ne '' && $paramDefault ne $oldDefaultValue && $hasValue == 1) {
				$res = modifyProcedureFormalParamDefaultValue($ec, $paramName, $targetProject, $targetProcedure, $paramDefault);
				if($res == 0) {
					print(" Error modifyGeneralSelectParam $paramName\n");
					print(" \tFailed to modify default value - $paramDefault\n");
					return 0;
				}
			}
		}
	} else {
		if($paramDefault eq '') {
			foreach my $optionName ( sort keys %{$optionList} ){
				$paramDefault = $optionList->{$optionName};
				last;
			}
		}
		print '[New Option List] : '. Dumper $optionList;
		$res = createSelectParam($ec, $paramName, $targetProject, $targetProcedure, $description, 1, $paramDefault, $optionList);
        if($res == 0) {
            print(" Error modifyGeneralSelectParam $paramName\n");
            print(" \tFailed to execute createSelectParam\n");
            return 0;
        }
	}

	return 1;
}

sub modifyProcedureFormalParamDefaultValue{
    my ($ec, $paramName, $projectName, $procedureName, $defaultValue) = @_;

    my($success, $xPath, $errMsg, $errCode) = invokeCommand($ec," SuppressLog IgnoreError " , 'getFormalParameter' ,
                                                             {
                                                             'projectName' => $projectName,
                                                             'procedureName' =>$procedureName,
															 'formalParameterName' => $paramName
                                                             });

    if (! $success) {
        printf(" Error modifyFormalParam - %s\n", $paramName);
        printf(" %s: %s\n", $errCode, $errMsg);
        return 0;
    }

    ($success, $xPath, $errMsg, $errCode) = invokeCommand($ec," SuppressLog IgnoreError " , 'modifyFormalParameter' ,
                                                            { 'projectName' => $projectName,
                                                             'procedureName' =>$procedureName, 
                                                             'formalParameterName' =>$paramName,
                                                             'defaultValue' => $defaultValue
                                                             });
    if (! $success) {
        printf(" Error modifyFormalParam - %s\n", $paramName);
        printf(" %s: %s\n", $errCode, $errMsg);
        return 0;
    } 

    return 1;
}

sub modifyProcedureFormalParamRequired {
    my ($ec, $paramName, $projectName, $procedureName, $required) = @_;

    my($success, $xPath, $errMsg, $errCode) = invokeCommand($ec," SuppressLog IgnoreError " , 'getFormalParameter' ,
                                                             {
                                                             'projectName' => $projectName,
                                                             'procedureName' =>$procedureName,
                                                             'formalParameterName' => $paramName
                                                             });

    if (! $success) {
        printf(" Error modifyFormalParam - %s\n", $paramName);
        printf(" %s: %s\n", $errCode, $errMsg);
        return 0;
    }

    ($success, $xPath, $errMsg, $errCode) = invokeCommand($ec," SuppressLog IgnoreError " , 'modifyFormalParameter' ,
                                                            { 'projectName' => $projectName,
                                                             'procedureName' =>$procedureName, 
                                                             'formalParameterName' =>$paramName,
                                                             'required' => $required
                                                             });
    if (! $success) {
        printf(" Error modifyFormalParam - %s\n", $paramName);
        printf(" %s: %s\n", $errCode, $errMsg);
        return 0;
    } 

    return 1;
}

sub modifySharedSelectParam {
    my ($ec,$sheetProject, $targetProject,$targetProcedureArray,$paramName,$paramDefault,$desc,$optionList,$needToReset) = @_; 

	my $res = 1;

	if($needToReset == 1) {
		deleteProperty($ec, "/projects/$sheetProject/sharedParam/$targetProject/parameters/$paramName");
	}

	foreach(@{$targetProcedureArray}) {
		unless(checkFormalParamExist($ec,$paramName, $targetProject, $_)) {
			if($paramDefault eq '') {
				foreach my $optionName ( sort keys %{$optionList} ){
					$paramDefault = $optionList->{$optionName};
					last;
				}
			}

			$res = createSharedSelectParamBase($ec, $paramName, $sheetProject, $targetProject,  $_, $desc, 1, $paramDefault);
			if($res == 0) {
				print(" Error modifySharedSelectParam $paramName\n");
				print(" \tFailed to execute createSharedSelectParam\n");
				return 0;
			}	
		}	
	}

	my $counter = 1;
	my $oldOptList = undef;

	if( checkWhetherPropertyExist( $ec, "/projects/$sheetProject/sharedParam/$targetProject/parameters/$paramName/optionCount") == 1 ) {
		$counter = getPropertyValue( $ec, "/projects/$sheetProject/sharedParam/$targetProject/parameters/$paramName/optionCount")+1;
		$oldOptList = getSharedSelectParamOptions($ec, $paramName, $sheetProject,$targetProject);
		if(defined $oldOptList) {
			my $optCount = keys %{$oldOptList};
			$optCount =$optCount + 1;
			if($optCount != $counter) {
				print(" Error modifySharedSelectParam $paramName\n");
				print(" \tOld optionCount and option's count doesn't match.\n");
				return 0;
			}
			print '[Old Option List] : '. Dumper $oldOptList;
			print "\n";

		} else {
			print(" Error modifySharedSelectParam $paramName\n");
			print(" \tOld optionCount and option's count doesn't match.\n");
			return 0;
		}
	}  else {
		print "[Old Option List] : empty\n";
		print "\n";
	}

    my $newCounter = 0;
    my @keys = sort keys %$optionList;
    foreach my $key ( @keys ) {
		if (defined $oldOptList){
			if (defined $oldOptList->{$key}) {
				next;
			}
		}

        $res = setProperty($ec, "/projects/$sheetProject/sharedParam/$targetProject/parameters/$paramName/option".$counter."/text", $key );
        if($res == 0) {
            print(" Error modifySharedSelectParam $paramName\n");
            print(" \tFailed to set option key - $key\n");
            return 0;
        }
        $res = setProperty( $ec, "/projects/$sheetProject/sharedParam/$targetProject/parameters/$paramName/option".$counter."/value", $optionList->{"$key"} );
        if($res == 0) {
            my $value = $optionList->{"$key"};
            print(" Error modifySharedSelectParam $paramName\n");
            print(" \tFailed to set option value - $value\n");
            return 0;
        }

        my $value = $optionList->{"$key"};
        print "[AddedOption] : KEY => $key - VALUE => $value\n\n";
        $counter ++;
        $newCounter ++;
    }

    @keys = sort keys %$oldOptList;
    foreach my $key ( @keys ) {
        unless (defined $optionList->{$key}) {
            $optionList->{$key} = $oldOptList->{$key};
        }
    }

	print '[New Option List] : '. Dumper $optionList;

    if( $newCounter > 0 ) {
        $res = setProperty($ec, "/projects/$sheetProject/sharedParam/$targetProject/parameters/$paramName/optionCount", $counter -1);
        if($res == 0) {
            print(" Error modifySharedSelectParam $paramName\n");
            print(" \tFailed to set optionCount\n");
            return 0;
        }
    }

    return 1;
}

sub setPropertySheetForSelectParam{    
    my ($ec, $paramName, $projectName, $propertySheetProj, $procedureName, $description, $required, $defaultValue, $options) = @_;
    my $counter = 1;

    @keys = keys %$options;
    foreach my $key ( @keys ) {
        #print "option : $option \n";
        $ec->setProperty( "/projects/$propertySheetProj/parameters/$paramName/option" . $counter . "/text" , $key );
        $ec->setProperty( "/projects/$propertySheetProj/parameters/$paramName/option" . $counter . "/value" , $options->{"$key"} );
        $counter ++;
    }

    $ec->setProperty("/projects/$propertySheetProj/parameters/$paramName/optionCount" , $counter - 1);
    $ec->setProperty("/projects/$projectName/procedures/$procedureName/ec_customEditorData/parameters/$paramName/formType" , "standard"); 
    $ec->setProperty("/projects/$projectName/procedures/$procedureName/ec_customEditorData/parameters/$paramName/options/propertyPath", "/projects/$propertySheetProj/parameters/$paramName");
    $ec->setProperty("/projects/$projectName/procedures/$procedureName/ec_customEditorData/parameters/$paramName/options/type" , 'entry');
    return 1;
}

