use ElectricCommander;

push(@::gMatchers,
    # Electric Cloud errors:
    # ERROR EC3123: ...
    {
		id => "ecloudError",
		pattern => q{^ERROR EC\d*:(.+)},
		action => q{
					my $description = ((defined $::gProperties{"/myJob/EA/EAsummary"}) ? $::gProperties{"/myJob/EA/EAsummary"} : '');
					$description .= "An error occurred: $1";          
					setProperty("/myJob/EA/EAsummary", $description . "\n");    
		},
	},

    # Build Interruption:
    # Interrupted build: 28832   Duration: 3:59 (m:s)   Cluster availability: 100%
	{
		id => "interruptedBuild",
		pattern => q{^Interrupted build:(.+)Duration:(.+)Cluster availability:(.+)},
		action => q{
