					$description .= "An error occurred: Build $1 has been interrupted \n Duration: $2 \n Cluster availability: $3"";  
					setProperty("/myJob/EA/EAsummary", $description . "\n");    

		},
	},

    #Makefile error:
    #makefile:3: *** missing separator.  Stop.
	{
		id => "makefileError",
		pattern => q{(.+):(\d+): (\**)(.+) Stop.},
		action => q{
					my $description = ((defined $::gProperties{"/myJob/EA/EAsummary"}) ? $::gProperties{"/myJob/EA/EAsummary"} : '');
					$description .= "An error occurred on  $1, line $2:$4";  
					setProperty("/myJob/EA/EAsummary", $description . "\n");    
		},
	},

    # Makeerror:
    # make: *** No rule to make target `x'.  Stop.
	{
		id => "makeError",
		pattern => q{make: (\**)(.+) Stop.},
		action => q{
					my $description = ((defined $::gProperties{"/myJob/EA/EAsummary"}) ? $::gProperties{"/myJob/EA/EAsummary"} : '');
					$description .= "An error occurred: $2";
					setProperty("/myJob/EA/EAsummary", $description . "\n");    

		},
	},

    # Finished Build:
    # Finished build: 44058   Duration: 0:28 (m:s)   Cluster availability: 100%
	{
		id => "finishedBuild",
		pattern => q{Finished build:(.+)Duration:(.+)Cluster availability:(.+)},
