use POSIX qw(strftime);

my @customMatchers = (
{ id => "stampLog",
     pattern => q{^(.*)$},
     action => q{displayWithTimeStamp($1);},
   },
);

unshift @::gMatchers,@customMatchers;

 sub displayWithTimeStamp($) {
     my $inputStr = shift;
#     my $dateStr = ''.time();
#     my $outStr = $dateStr . ': ' . $inputStr;
     my $outStr = $inputStr;

     if (! $::gstamp_init) {
         my $xp = $::gCommander->getProperty('/javascript getProperty("/myProject/dtlogFileName")');
         my $lfn = $xp->findvalue('/responses/response/property/value');
         if ($lfn) {
             if (open STAMPLOG, '>>' . $lfn) {
                 $::gstamp_lfn = $lfn;
             }
         }
         $::gstamp_init = 1;
     }
     if ($::gstamp_lfn) {
         print STAMPLOG $outStr . "\n";
     } else {
         push(@::displayText, $outStr);
         setProperty("postSummary", join("\n", @::displayText));
         my $num = @::displayText;
         if($num>300) { 
            shift @::displayText;
         }
     }
 };

use ElectricCommander;
$::gPackages = "";

my @customMatchers = (
    {
      id => "YoctoBuildError",
     pattern => q{ERROR: ([^ ]+)-(\d+)([^ ]+) do_},
     action => q{
       my $pkg = $1;
       if(index($::gPackages, $pkg) == -1) {
         $::gPackages = $::gPackages. " " . $pkg;
       }
       setProperty("/myJob/$[/myParent/procedureName]/YoctoBuildRetry",1);
       incValue("errors"); diagnostic("", "error", -4, forwardWhile("^   "))}
    }
);

unshift @::gMatchers,@customMatchers;

sub postpEndHook()
{
  if($::gPackages eq "" ) {
    setProperty("/myJob/$[/myParent/procedureName]/YoctoBuildRetry",0);
  }
  setProperty("/myJob/$[/myParent/procedureName]/YoctoBuildErrorComponents",$::gPackages);
}

sub checkWhetherProcedureExist {
	my $ec = shift;
	my $projectName = shift;
	my $procedureName = shift;

    my($success, $xPath, $errMsg, $errCode)= invokeCommand($ec, "SuppressLog IgnoreError", "getProcedures", { 'projectName' => "$projectName"});	
	if (! $success) {
        printf(" Error runProcedure - projects : %s , procedure : %s", $projectName, $procedureName);
        printf(" %s: %s\n", $errCode, $errMsg);
        return 0;
    } 

	my $nodeset = $xPath->findnodes("//procedure");

	my @procedureNames = ();
	push(@procedureNames, $_->findvalue("procedureName")) foreach ($nodeset->get_nodelist);

	foreach (@procedureNames) {
		if($procedureName eq $_) {
			return 1;
		}
	}

	return 0;
}

sub cloneProcedure {
	my $ec = shift;
	my $projectName = shift;
	my $srcProcedureName = shift;
	my $destProcedureName = shift;

    my($success, $xPath, $errMsg, $errCode)= invokeCommand($ec, "SuppressLog IgnoreError", "clone", {'projectName' => $projectName,  'procedureName' => $srcProcedureName, 'cloneName' => $destProcedureName});    
    if (! $success) {
        printf(" Error cloneProcedure - projects : %s , procedure : %s", $projectName, $srcProcedureName);
        printf(" %s: %s\n", $errCode, $errMsg);
        return 0;
    }

	$ec->modifyProcedure($projectName, $destProcedureName, {description =>""});

	return 1;	
}

sub deleteProcedure {
	my $ec = shift;
	my $projectName = shift;
	my $procedureName = shift;

    my($success, $xPath, $errMsg, $errCode)= invokeCommand($ec, "SuppressLog IgnoreError", "deleteProcedure", { 'projectName' => "$projectName", 'procedureName' => "$procedureName"});    
    if (! $success) {
        printf(" Error deleteProcedure - projects : %s , procedure : %s", $projectName, $procedureName);
        printf(" %s: %s\n", $errCode, $errMsg);
        return 0;
    }

	return 1;	
}

sub getProcedureNames {
    my $ec = shift;
    my $projectName = shift; #user or group 

    my ($success, $res, $errMsg, $errCode)= invokeCommand($ec, "SuppressLog IgnoreError", "getProcedures", { 'projectName' => $projectName}); 
    if (! $success) {
        print("  Error getting workflow definitoins => ProjectName : $projectName");
        print(" $errCode: $errMsg\n");
        return undef ;
    }

    #print $res ->findnodes_as_string('/') . "\n";

=pod ==> Parsing Below Information
    <procedure>
      <procedureId>f60834e0-791f-11e8-b106-462f20524153</procedureId>
      <procedureName>배포 프로시저</procedureName>
      <createTime>2018-06-26T09:03:30.165Z</createTime>
      <description />
      <jobNameTemplate />
      <lastModifiedBy>PLMTESTBE1</lastModifiedBy>
      <modifyTime>2018-06-26T09:03:30.165Z</modifyTime>
      <owner>admin</owner>
      <projectName>IBOX_Android3</projectName>
      <propertySheetId>f60834e2-791f-11e8-b106-462f20524153</propertySheetId>
      <resourceName />
      <timeLimit />
      <timeLimitUnits>minutes</timeLimitUnits>
      <tracked>0</tracked>
      <workspaceName />
    </procedure>
=cut

    my $nodeset = $res->findnodes("//procedure");    

    my @procedureNames;
    foreach ($nodeset->get_nodelist) {

        push @procedureNames, $_->findvalue("procedureName");
    }

    return \@procedureNames;
}

sub getProceduresArray {
	my $ec = shift; 
	my $projectName = shift;

	my($success, $xPath, $errMsg, $errCode) = invokeCommand($ec," SuppressLog IgnoreError " , 'getProcedures', $projectName);    
	if (! $success) {
		print(" Error - getProceduresRef \n");
		printf(" %s: %s\n", $errCode, $errMsg);
		return undef;
	} 

	my @procedures = $xPath->findnodes("//procedure");

	return \@procedures;
}

sub runProcedure {
    my ($ec, $projectName, $procedureName, $actualParameters) = @_;

    if(defined $actualParameters) {
        my $xPathParams = $ec->getFormalParameters( {'projectName' => $projectName, 'procedureName' => $procedureName});
        my @nodes = $xPathParams->findnodes("//formalParameter");
        my @formalParamLists = (); 
        foreach my $node (@nodes) { 
            my $paramName = $node->findvalue("formalParameterName");
            unless(defined $actualParameters->{"$paramName"}){
                my $actualParameterValue = $node->findvalue("defaultValue");
                $actualParameters->{"$paramName"} = "$actualParameterValue";
            }
        }
    }

    my @actualParameterAOH = ();

    my $success = undef;
    my $xPath = undef;
    my $errMsg = undef;
    my $errCode = undef;

    if(defined $actualParameters) {
        foreach my $actualParameterName ( sort keys %{$actualParameters} )
        {
            my $actualParameterValue = $actualParameters->{$actualParameterName};
            push @actualParameterAOH, { actualParameterName => "$actualParameterName", value => "$actualParameterValue"};
        }

        ($success, $xPath, $errMsg, $errCode)= invokeCommand($ec, "SuppressLog IgnoreError", "runProcedure", { 'projectName' => "$projectName", 'procedureName' => "$procedureName", 'actualParameter' => \@actualParameterAOH });
        if (! $success) {
            print(" Error runProcedure - projects : $projectName , procedure : $procedureName\n");
            print(" $errCode: $errMsg\n");
            return undef;
        }

    } else {
        ($success, $xPath, $errMsg, $errCode)= invokeCommand($ec, "SuppressLog IgnoreError", "runProcedure", { 'projectName' => "$projectName", 'procedureName' => "$procedureName"});
        if (! $success) {
            print(" Error runProcedure - projects : $projectName , procedure : $procedureName\n");
            print(" $errCode: $errMsg\n");
            return undef;
        } 
    }

    my $jobID = $xPath->find("//response/jobId");

    return $jobID;
}

sub runProcedureAndWaitCompleted {
    my ($ec, $projectName, $procedureName, $actualParameters) = @_;

    if(defined $actualParameters) {
        my $xPathParams = $ec->getFormalParameters( {'projectName' => $projectName, 'procedureName' => $procedureName});
        my @nodes = $xPathParams->findnodes("//formalParameter");
        my @formalParamLists = (); 
        foreach my $node (@nodes) { 
            my $paramName = $node->findvalue("formalParameterName");
            unless(defined $actualParameters->{"$paramName"}){
                my $actualParameterValue = $node->findvalue("defaultValue");
                $actualParameters->{"$paramName"} = "$actualParameterValue";
            }
        }
    }

    my @actualParameterAOH = ();

    my $success = undef;
    my $xPath = undef;
    my $errMsg = undef;
    my $errCode = undef;

	print "RunProcedure - projects : $projectName , procedure : $procedureName\n";

    if(defined $actualParameters) {
        foreach my $actualParameterName ( sort keys %{$actualParameters} )
        {
            my $actualParameterValue = $actualParameters->{$actualParameterName};
            push @actualParameterAOH, { actualParameterName => "$actualParameterName", value => "$actualParameterValue"};
        }

        ($success, $xPath, $errMsg, $errCode)= invokeCommand($ec, "SuppressLog IgnoreError", "runProcedure", { 'projectName' => "$projectName", 'procedureName' => "$procedureName", 'actualParameter' => \@actualParameterAOH });
        if (! $success) {
            print(" Error runProcedure - projects : $projectName , procedure : $procedureName\n");
            print(" $errCode: $errMsg\n");
            return undef;
        }

    } else {
        ($success, $xPath, $errMsg, $errCode)= invokeCommand($ec, "SuppressLog IgnoreError", "runProcedure", { 'projectName' => "$projectName", 'procedureName' => "$procedureName"});
        if (! $success) {
            print(" Error runProcedure - projects : $projectName , procedure : $procedureName\n");
            print(" $errCode: $errMsg\n");
            return undef;
        } 
    }

    my $jobID = $xPath->find("//response/jobId");

	my $jobState = "";

    do {
        $jobState = $ec->getJobStatus($jobID)->findvalue('//status')->value();
        sleep(1);
    } while ($jobState ne "completed");

    #$ec->waitForJob($jobID, undef, "completed");

    my $jobDetails = $ec->getJobDetails($jobID);
    my @steps = $jobDetails->findnodes("//jobStep");
    foreach my $s (@steps) {
        my $stepName = $s->findvalue("stepName")->value;
        my $step_outcome = $s->findvalue("outcome")->value;
        my $step_status = $s->findvalue("status")->value;
        print "\t[stepName] : $stepName:\n";
        print "\t\t outcome => $step_outcome\n";
        print "\t\t status => $step_status\n";
    }

    return $jobID;
}

sub waitProcedureUntilCompleted {
	my $ec = shift;
	my $jobID = shift;
	my $jobState = "";

    do {
        $jobState = $ec->getJobStatus($jobID)->findvalue('//status')->value();
        sleep(0.5);
    } while ($jobState ne "completed");

	my $result = 1;

    my $jobDetails = $ec->getJobDetails($jobID);
    my @steps = $jobDetails->findnodes("//jobStep");
    foreach my $s (@steps) {
        my $stepName = $s->findvalue("stepName")->value;
        my $step_outcome = $s->findvalue("outcome")->value;
        my $step_status = $s->findvalue("status")->value;
        print "\t[stepName] : $stepName:\n";
        print "\t\t outcome => $step_outcome\n";
        print "\t\t status => $step_status\n";

		if($step_outcome eq "success" || $step_outcome eq "skipped") {
			next;
		}
		$result = 0;
    }

	return $result;
}

package Importer;
use strict;
use warnings;
use utf8; 
use JSON;
use Switch; 
