  my $ec = shift;
  my $prop = shift;

  my($success, $xPath, $errMsg, $errCode)= invokeCommand($ec, "SuppressLog IgnoreError", "getProperty", $prop);

  if (! $success) {
    return 0;
  } 

  return 1;
}

sub createPropertySheet {
  my $ec = shift;
  my $propSheetName = shift;

  my($success, $xPath, $errMsg, $errCode)= invokeCommand($ec, "SuppressLog IgnoreError", "createProperty",{ propertyName => "$propSheetName", propertyType => "sheet"});
  if (! $success) {
    printf(" Error createPropertySheet - %s", $propSheetName);
    printf(" %s: %s\n", $errCode, $errMsg);
    return 0;
  } 

  return 1;
}

sub deleteProperties {
    my ($ec,$path, $keepRoot) = @_;

    my($success, $result, $errMsg, $errCode) = invokeCommand($ec, "SuppressLog IgnoreError", "getProperties", {'path'=>$path});
    my $nodeset=$result->find('//property');

    foreach ($nodeset->get_nodelist) {
        my $propName=$result->findvalue('propertyName', $_);
        my $value =$result->findvalue('value', $_);
        my $psId =$result->findvalue('propertySheetId', $_);

		if (!deleteProperty($ec,"$path/$propName")) {  
			print("Error deletePropertiesRecursively -  $path\n");
			print("$errCode: $errMs \n");
			return 0;
		}
    }

	unless( defined $keepRoot ) {
		if (!deleteProperty($ec,"$path")) {  
			print("Error deletePropertiesRecursively -  $path\n");
			print("$errCode: $errMs \n");
			return 0;
		}else{
		    print("Deleted PropertySheet : $path \n");
		}
	}

	return 1;
}

sub deleteProperty {
  my $ec = shift;
  my $propertyPath = shift;

  my($success, $xPath, $errMsg, $errCode)= invokeCommand($ec, "SuppressLog IgnoreError", "deleteProperty", $propertyPath);

  if (! $success) {
    printf(" Error deleteProperty - %s", $propertyPath);
    printf(" %s: %s\n", $errCode, $errMsg);
    return 0;
  } 

  return 1;
}

sub getJobProperties {
    my ($ec, $jobID) = @_;
	my %properties = ();

	my $propertySheet = $ec->getProperties({'jobId'=>$jobID, 'recurse' => 1   });

    foreach my $node ($propertySheet->findnodes("//property/value") ) {
        my $propertyName = $propertySheet->findvalue("../propertyName", $node);
        my $propertyValue = $propertySheet->findvalue("../value", $node);
		$properties{"$propertyName"} = "$propertyValue";
    }

	 return \%properties;
}

sub getNestedPropertySheetNames {
    my $ec = shift;
    my $psParentPath=shift;

    my($success, $result, $errMsg, $errCode) = invokeCommand($ec, "SuppressLog IgnoreError", "getProperties", {'path'=>$psParentPath});
    if (! $success) {
		printf(" Error getNestedPropertySheetNames - %s", $psPath);
		printf(" %s: %s\n", $errCode, $errMsg);
		return undef;
	} 

	my @propSheetNames =();
    my $nodeset=$result->find('//property');

    foreach my $node ($nodeset->get_nodelist) {
        my $propName=$result->findvalue('propertyName', $node);
        my $psId =$result->findvalue('propertySheetId', $node);
        if ($psId ne '') {
			push(@propSheetNames, $propName);
        }
    }

    return @propSheetNames;
}

sub getPropertiesRecursively {
    my ($ec,$path,  $tree) = @_;

	my($success, $result, $errMsg, $errCode) = invokeCommand($ec, "SuppressLog IgnoreError", "getProperties", {'path'=>$path});
    my $nodeset=$result->find('//property');

    foreach ($nodeset->get_nodelist) {
        my $propName=$result->findvalue('propertyName', $_);
        my $value =$result->findvalue('value', $_);
        my $psId =$result->findvalue('propertySheetId', $_);

        my $key = $propName;

        if ($psId ne '') { 
            $tree->{"$propName"} = {};
			getPropertiesRecursively($ec,  "$path/$propName", $tree->{$propName});
        } else { 
            $tree->{"$propName"} = "$value";
        }
    }
}

sub getPropertiesRefRecursively {
	my $ec = shift;
	my $path = shift; 

	my %properties = ();
	getPropertiesRecursively ($ec,"$path",\%properties);
	my $size = keys %properties;
	unless ($size > 0) {
		return undef;
	}

	return \%properties;
}

