sub setPropertiesFromHash {
    my ($ec,$path, $tree) = @_;

    foreach (sort keys %{$tree}) {
        my $propValue = $tree->{$_};

        if (ref $propValue eq ref {}) {
            setPropertiesFromHash($ec, "$path/$_", $propValue);
        } else {
			my($success, $xPath, $errMsg, $errCode)= invokeCommand($ec, "SuppressLog IgnoreError", "setProperty",{ propertyName => "$path/$_", value => "$propValue"});
			if (! $success) {
				printf(" Error setPropertiesFromHash - %s\n", "$path/$_");
				printf(" %s: %s\n", $errCode, $errMsg);
				return 0;
			} 
        }
    }

	return 1;
}

