     my $filePath = shift;
	 my ($fileNameOnly, $dir, $ext) = fileparse($filePath, '\.[^.]*');
     $dir =~ s/\\/\//g;
     $dir =~ s/\/\//\//g;
     $ext =~ s/\.//g;

	 my $size = getFileSize($filePath);

	 return ($fileNameOnly, $dir, $ext, $size);
}

