my $ec = new ElectricCommander();
my $buildClassName = '$[--emake-class]'; 

my $username= $ec->getFullCredential("eaLogin", {value => "password"})->findnodes("//userName")->string_value();
my $password= $ec->getFullCredential("eaLogin", {value => "userName"})->findnodes("//password")->string_value();

if(loginCmTool($username, $password) == 0) { 
	CHECK_EF_FUNC_ERROR($ec, "setProperty", "/myJob/EA/AGENT_STATUS/SERVER_CONNECT", "FAIL"); 
	CHECK_EF_FUNC_ERROR($ec, "setProperty", "/myJob/EA/USE_EMAKE", "0"); 
	exit 0;
}


my $resName = getBuildClassResourceName($buildClassName); 
if($resName eq ''){
	CHECK_EF_FUNC_ERROR($ec, "setProperty", "/myJob/EA/AGENT_STATUS/SERVER_CONNECT", "FAIL"); 
	CHECK_EF_FUNC_ERROR($ec, "setProperty", "/myJob/EA/USE_EMAKE", "0"); 
	exit 0;
}

my $hosts = getResoucesHostMasks($resName);
my ($total, $disabled, $live, $dead, $nohb, $waiting, $using, $waitingFromRes) = checkEntireAgentStatusFromHosts($hosts);

#my ($total, $disabled, $live, $dead, $nohb, $waiting) = checkEntireAgentStatus();

CHECK_EF_FUNC_ERROR($ec, "setProperty", "/myJob/EA/AGENT_STATUS/SERVER_CONNECT", "SUCCESS");
CHECK_EF_FUNC_ERROR($ec, "setProperty", "/myJob/EA/AGENT_STATUS/TOTAL", $total); 
CHECK_EF_FUNC_ERROR($ec, "setProperty", "/myJob/EA/AGENT_STATUS/DISABLED", $disabled); 
CHECK_EF_FUNC_ERROR($ec, "setProperty", "/myJob/EA/AGENT_STATUS/LIVE", $live); 
CHECK_EF_FUNC_ERROR($ec, "setProperty", "/myJob/EA/AGENT_STATUS/DEAD", $dead); 
CHECK_EF_FUNC_ERROR($ec, "setProperty", "/myJob/EA/AGENT_STATUS/NO_HEARTBEAT", $nohb); 
CHECK_EF_FUNC_ERROR($ec, "setProperty", "/myJob/EA/AGENT_STATUS/WAITING", $waiting); 
CHECK_EF_FUNC_ERROR($ec, "setProperty", "/myJob/EA/AGENT_STATUS/USING", $using); 
CHECK_EF_FUNC_ERROR($ec, "setProperty", "/myJob/EA/AGENT_STATUS/WAITING_FROM_RES", 	$waitingFromRes); 

if($waitingFromRes >= $[/myProject/EA/EA_AGENT_LIMITS_FOR_A_BUILD] + 0) {
	if($using <= $[/myProject/EA/EA_MAX_AGENTS] - $[/myProject/EA/EA_AGENT_LIMITS_FOR_A_BUILD]) {
		CHECK_EF_FUNC_ERROR($ec, "setProperty", "/myJob/EA/USE_EMAKE", 1); 
		exit 0;
	}
}

CHECK_EF_FUNC_ERROR($ec, "setProperty", "/myJob/EA/USE_EMAKE", 0);

