my $ec = new ElectricCommander();

my $emakeRoot = sortPathSep('$[ROOT_DIR]');
my $outName = '$[OUTPUT_DIR]'; 

my $srcJavaDocFile = sortPathSep("/DATA/workspace/EA_DATA/EA_CODE/AndroidTemplate/cachejavadoc.mk");
my $dstCacheJavaDocFile = sortPathSep("$emakeRoot/cachejavadoc.mk");
`sed -e 's/out/$outName/' < $srcJavaDocFile > $dstCacheJavaDocFile`;

my $srcNoAutoDepFile = sortPathSep("/DATA/workspace/EA_DATA/EA_CODE/AndroidTemplate/noautodep.mk");
my $dstNoAutoDepFile = sortPathSep("$emakeRoot/noautodep.mk");
`sed -e 's/out/$outName/' < $srcNoAutoDepFile > $dstNoAutoDepFile`;

my $srcSensitivityFile = sortPathSep("/DATA/workspace/EA_DATA/EA_CODE/AndroidTemplate/sensitivity.mk");
my $dstSensitivityFile = sortPathSep("$emakeRoot/sensitivity.mk");
`sed -e 's/out/$outName/' < $srcSensitivityFile > $dstSensitivityFile`;

if( checkExistFileOrDir("$emakeRoot/cachejavadoc.mk")==0 ||
	checkExistFileOrDir("$emakeRoot/noautodep.mk")==0 ||
	checkExistFileOrDir("$emakeRoot/sensitivity.mk")==0 ) {
	print "Failed to copy with replacing output directory..\n";
	exit 1;	
}

print "Succeed to copy with replacing output directory..\n";

