sub checkEmakeOptions { 
	my $emake_cm = shift;
	my $emake_root =  shift;
	my $emake_class =  shift;
	my $emake_use_jobcache = shift;
	my $emake_jobcache =  shift; 
	my $emake_use_sharedcache = shift;
	my $emake_asset_dir =  shift;
    my $emake_history = shift;
    my $emake_historyfile = shift;
	my $emake_use_shared_history = shift;

	if($emake_cm eq ""){
		print "Please put Cluster Manager Host.\n";
		exit 1;
	}

	if($emake_root eq "" || checkExistFileOrDir($emake_root) == 0){
		print "Please put EMAKE ROOT which already exists.\n";
		exit 1;
	}

	if($emake_class eq ""){
		print "Please put Emake Build Class which already registerd in cluster manager.\n";
		exit 1;
	}

	if($emake_history eq "read" && $emake_use_shared_history eq '1') {
		if($emake_historyfile eq "" || checkExistFileOrDir($emake_historyfile) == 0){ 
			print "Please put SHARED HISTORY FILE PATH which already exists.\n";
			exit 1;
		}
	}

	if($emake_use_jobcache eq "1") {
		if($emake_jobcache eq ""){
			print "Please put  JOB CACHE TARGET if you want to use job cache.\n";
			exit 1;
		}

		if($emake_use_sharedcache eq "1") {
			if($emake_asset_dir eq "" ){
				print "Please put SHARED ASSET DIRECTORY which already exist if you want to use shared job cache.\n";
				exit 1;
			}
		}
	}
}


