sub createEmakeOptions {
	my $ec = shift;
	my $emake_cm = shift;
	my $emake_root =  shift;
	my $emake_class =  shift;
	my $emake_use_learning_build = shift;
	my $emake_build_label = shift;
	my $emake_pragma_file = shift;
	my $emake_autodepend =  shift;
	my $emake_optimize_deps =  shift;
	my $emake_optimize_schedule =  shift;
	my $emake_history_force = shift;
	my $emake_parse_avoidance =  shift;
	my $emake_parse_avoidance_ignore_env = shift;
	my $eamke_suppress_include = shift;
	my $emake_readdir_conflicts =  shift; 
	my $emake_use_sharedcache = shift;
	my $emake_jobcache =  shift; 
	my $emake_asset_dir =  shift; 
	my $emake_history =  shift; 
	my $emake_historyfile =  shift;
	my $emake_use_shared_history = shift;
	my $eamke_additional_options = shift;
	my $branchName =  shift; 

	my $targetProjName = '$[/myParent/parent/parent/parent/projectName]';
	my $targetProcName = '$[/myParent/parent/parent/parent/procedureName]';

	my $emake_history_copy_file= "";
    my $emake_asset_dir_copy = "";

	if($branchName && "$branchName" ne "") {
		$emake_history_copy_file= "$[/myProject/EA/EA_DATA_LOCAL_ROOT]/HISTORY/$targetProjName"."_"."$targetProcName"."_"."$branchName.data";
		$emake_asset_dir_copy = "$[/myProject/EA/EA_DATA_LOCAL_ROOT]/ASSET/$targetProjName"."_"."$targetProcName"."_"."$branchName";
	} else {
		$emake_history_copy_file= "$[/myProject/EA/EA_DATA_LOCAL_ROOT]/HISTORY/$targetProjName"."_"."$targetProcName.data";
		$emake_asset_dir_copy = "$[/myProject/EA/EA_DATA_LOCAL_ROOT]/ASSET/$targetProjName"."_"."$targetProcName";
	}

    my @args = ();

    if($emake_cm && $emake_cm ne '') {
        push(@args, '--emake-cm=' . $emake_cm);
    }

    if($emake_root && $emake_root ne '') {
		my $annoDir = "$[/myProject/EA/EA_DATA_LOCAL_ROOT]/ANNO";
		if(createDirectoryIfNotExists($annoDir, '0777') == 0) {
				print "Couldn't create anno directory : $annoDir\n";
				exit 1; 
		}

		my $annofile = $annoDir.'/@ECLOUD_BUILD_ID@.anno';
		my $logfile = $annoDir.'/@ECLOUD_BUILD_ID@.dlog';

		$emake_root = sortPathSep($emake_root);
		$annofile = sortPathSep($annofile);
		$logfile = sortPathSep($logfile);

		push(@args, '--emake-root='. $emake_root);
		push(@args, '--emake-annofile='. $annofile);
		push(@args, '--emake-logfile='. $logfile);
    }

    if($emake_class && $emake_class ne '') {
        push(@args, '--emake-class=' . $emake_class);
    }

	#push(@args, '--emake-debug="fjh"');	
    push(@args, '--emake-blind-create=1');


    if($emake_use_learning_build && $emake_use_learning_build ne '') {
        push(@args, '--emake-maxagents=8');
		push(@args, '--emake-readdir-conflicts=1');
    }

    if($emake_build_label && $emake_build_label ne '') {
        $emake_build_label =~ s/ /_/;
        push(@args, '--emake-build-label=' . $emake_build_label);
    }

    if($emake_pragma_file && $emake_pragma_file ne '') {
        push(@args, '--emake-pragmafile=' . sortPathSep($emake_pragma_file));
    }

    if($emake_autodepend ne '') {
        push(@args, '--emake-autodepend=' . $emake_autodepend);
    }

    if($emake_optimize_deps ne '') {
        push(@args, '--emake-optimize-deps=' . $emake_optimize_deps);
    }

    if($emake_optimize_schedule ne '') {
        push(@args, '--emake-optimize-schedule=' . $emake_optimize_schedule);
    }

    if($emake_parse_avoidance ne '') {
        push(@args, '--emake-parse-avoidance=' . $emake_parse_avoidance);
    }

    if($emake_history_force ne '') {
        push(@args, '--emake-history-force=' . $emake_history_force);
    }

    #if($emake_readdir_conflicts ne '') {
    #    push(@args, '--emake-readdir-conflicts=' . $emake_readdir_conflicts);
    #}

    if($emake_asset_dir && $emake_asset_dir ne '') {
		$emake_asset_dir = sortPathSep($emake_asset_dir);

		if($emake_use_sharedcache eq '0') {
			if(createDirectoryIfNotExists($emake_asset_dir, '0777') == 0) {
				print "Couldn't create asset directory : $emake_asset_dir\n";
				exit 1; 
			}

			push(@args, '--emake-assetdir=' . $emake_asset_dir);		
		} else {
			if(createDirectoryIfNotExists($emake_asset_dir_copy, '0777') == 0) {
				print "Couldn't create local asset directory : $emake_asset_dir_copy\n";
				exit 1; 
			}

			push(@args, '--emake-shared-assetdir=' . $emake_asset_dir);		
			push(@args, '--emake-assetdir=' . $emake_asset_dir_copy);		
		}
    }

    if($emake_jobcache && $emake_jobcache ne '') {
        push(@args, '--emake-jobcache=' . $emake_jobcache);
    }

    if($emake_history && $emake_history ne '') {
        push(@args, '--emake-history=' . $emake_history);
    }

	if($emake_use_learning_build eq '0'){
		#if(! -e $emake_historyfile && ! -e $emake_history_copy_file) {
		#	print "There is no learning build data yet. Please run learning build first..\n";
		#	exit 1;
		#}

		if(! -e $emake_history_copy_file){
			#my %params = ();
			#$params{"sourceFile"} = $emake_historyfile;
			#$params{"destinationFile"} = $emake_history_copy_file; 
			#CHECK_EF_FUNC_ERROR($ec, "createJobStep", "CO_CONF_EA", "CO_File_Copy", "History File Copy", \%params); 
			push(@args, '--emake-maxagents=10');
			push(@args, '--emake-readdir-conflicts=1');
		} else {
			push(@args, '--emake-readdir-conflicts=0');
		}

		$emake_historyfile = $emake_history_copy_file; 
	}

	if($emake_historyfile && $emake_historyfile ne '') {
        $emake_historyfile = sortPathSep($emake_historyfile);

		my $historyPath  = dirname($emake_historyfile);
		if(createDirectoryIfNotExists($historyPath, '0777') == 0) {
			print "Couldn't create history directory : $historyPath";
			exit 1; 
		}

		push(@args, '--emake-historyfile=' . $emake_historyfile);
	}

    push(@args, '--emake-suppress-include=*.d');
    push(@args, '--emake-suppress-include=*.P');

    if($emake_parse_avoidance_ignore_env  && $emake_parse_avoidance_ignore_env  ne '') {
		$emake_parse_avoidance_ignore_env =~ s/^\s+|\s+$//g;
		my @emake_parse_avoidance_ignore_env_arr = split("\n",$emake_parse_avoidance_ignore_env);
        foreach my $emakeCommand (@emake_parse_avoidance_ignore_env_arr) {
			 push(@args, '--emake-parse-avoidance-ignore-env=' . "$emakeCommand");
        }
    }

    if($eamke_additional_options  && $eamke_additional_options  ne '') {
		$eamke_additional_options =~ s/^\s+|\s+$//g;
		my @eamke_additional_option_arr = split("\n",$eamke_additional_options);
        foreach my $emakeCommand (@eamke_additional_option_arr) {
            push(@args, "$emakeCommand");
        }
    }

	my $result = join(" \\\n", @args);

	print "EA OPTIONS : \n";
	print "$result\n";

	CHECK_EF_FUNC_ERROR($ec, "setProperty", "/myJob/$targetProcName/EA/emakeCommandLine", $result); 
}


my $ec = new ElectricCommander();  
my $emake_cm = '$[--emake-cm]';
my $emake_root = '$[--emake-root]';
my $emake_class = '$[--emake-class]';
my $emake_autodepend = '$[--emake-autodepend]';
my $emake_optimize_deps = '$[--emake-optimize-deps]';
my $emake_optimize_schedule = '$[--emake-optimize-schedule]';
my $emake_parse_avoidance = '$[--emake-parse-avoidance]';
my $emake_parse_avoidance_ignore_env = '$[--emake-parse-avoidance-ignore-env]';
my $emake_readdir_conflicts = '$[--emake-readdir-conflicts]'; 
my $emake_use_jobcache = '$[--emake-use-jobcache]'; 
my $emake_jobcache = '$[--emake-jobcache]'; 
my $emake_use_sharedcache = '$[--emake-use-shared-jobcache]'; 
my $emake_asset_dir = '$[--emake-shared-assetdir]';
my $emake_build_label = '$[/myJob/jobName]';
my $emake_pragma_file = '$[--emake-pragmafile]';
my $eamke_suppress_include = '$[--emake-suppress-include]';
my $eamke_additional_options = '$[--emake-additional-options]';
my $emake_history_force = '$[--emake-history-force]';
my $emake_use_learning_build = '$[--emake-use-learning-build]';
my $emake_history = '$[--emake-history]';
my $emake_historyfile = '$[--emake-historyfile]';
my $emake_use_shared_history = '$[--emake-use-shared-history]';
my $branchName = '$[BRANCH_NAME]';

createEmakeOptions(
$ec,
$emake_cm, 
$emake_root, 
$emake_class,
$emake_use_learning_build,
$emake_build_label, 
$emake_pragma_file,
$emake_autodepend, 
$emake_optimize_deps, 
$emake_optimize_schedule,
$emake_history_force,
$emake_parse_avoidance,
$emake_parse_avoidance_ignore_env,
$eamke_suppress_include,
$emake_readdir_conflicts,
$emake_use_sharedcache,
$emake_jobcache,
$emake_asset_dir,
$emake_history,
$emake_historyfile,
$emake_use_shared_history,
$eamke_additional_options,
$branchName);

step.description:
$[/myProject/scripts/head] 
$[/myProject/scripts/commanderFuncs/CHECK_EF_FUNC_ERROR]
$[/myProject/scripts/commanderFuncs/setResultAndError]
$[/myProject/scripts/commanderFuncs/invokeCommand]
$[/myProject/scripts/propertyFuncs/setProperty] 
$[/myProject/scripts/utilityFuncs/sortPathSep]
$[/myProject/scripts/utilityFuncs/createDirectoryIfNotExists]
$[/myProject/scripts/jobFuncs/createJobStep]

sub createEmakeOptions {
	my $ec = shift;
	my $emake_cm = shift;
	my $emake_root =  shift;
	my $emake_class =  shift;
	my $emake_use_learning_build = shift;
	my $emake_build_label = shift;
	my $emake_pragma_file = shift;
	my $emake_autodepend =  shift;
	my $emake_optimize_deps =  shift;
	my $emake_optimize_schedule =  shift;
	my $emake_history_force = shift;
	my $emake_parse_avoidance =  shift;
	my $emake_parse_avoidance_ignore_env = shift;
	my $eamke_suppress_include = shift;
	my $emake_readdir_conflicts =  shift; 
	my $emake_use_sharedcache = shift;
	my $emake_jobcache =  shift; 
	my $emake_asset_dir =  shift; 
	my $emake_history =  shift; 
	my $emake_historyfile =  shift;
	my $emake_use_shared_history = shift;
	my $eamke_additional_options = shift;
	my $branchName =  shift; 

	my $targetProjName = '$[/myParent/parent/parent/parent/projectName]';
	my $targetProcName = '$[/myParent/parent/parent/parent/procedureName]';

	my $emake_history_copy_file= "";
    my $emake_asset_dir_copy = "";

	if($branchName && "$branchName" ne "") {
		$emake_history_copy_file= "$[/myProject/EA/EA_DATA_LOCAL_ROOT]/HISTORY/$targetProjName"."_"."$targetProcName"."_"."$branchName.data";
		$emake_asset_dir_copy = "$[/myProject/EA/EA_DATA_LOCAL_ROOT]/ASSET/$targetProjName"."_"."$targetProcName"."_"."$branchName";
	} else {
		$emake_history_copy_file= "$[/myProject/EA/EA_DATA_LOCAL_ROOT]/HISTORY/$targetProjName"."_"."$targetProcName.data";
		$emake_asset_dir_copy = "$[/myProject/EA/EA_DATA_LOCAL_ROOT]/ASSET/$targetProjName"."_"."$targetProcName";
	}

    my @args = ();

    if($emake_cm && $emake_cm ne '') {
        push(@args, '--emake-cm=' . $emake_cm);
    }

    if($emake_root && $emake_root ne '') {
		my $annoDir = "$[/myProject/EA/EA_DATA_LOCAL_ROOT]/ANNO";
		if(createDirectoryIfNotExists($annoDir, '0777') == 0) {
				print "Couldn't create anno directory : $annoDir\n";
				exit 1; 
		}

		my $annofile = $annoDir.'/@ECLOUD_BUILD_ID@.anno';
		my $logfile = $annoDir.'/@ECLOUD_BUILD_ID@.dlog';

		$emake_root = sortPathSep($emake_root);
		$annofile = sortPathSep($annofile);
		$logfile = sortPathSep($logfile);

		push(@args, '--emake-root='. $emake_root);
		push(@args, '--emake-annofile='. $annofile);
		push(@args, '--emake-logfile='. $logfile);
    }

    if($emake_class && $emake_class ne '') {
        push(@args, '--emake-class=' . $emake_class);
    }

	#push(@args, '--emake-debug="fjh"');	
    push(@args, '--emake-blind-create=1');


    if($emake_use_learning_build && $emake_use_learning_build ne '') {
        push(@args, '--emake-maxagents=8');
		push(@args, '--emake-readdir-conflicts=1');
    }

    if($emake_build_label && $emake_build_label ne '') {
        push(@args, '--emake-build-label=' . $emake_build_label);
    }

    if($emake_pragma_file && $emake_pragma_file ne '') {
        push(@args, '--emake-pragmafile=' . sortPathSep($emake_pragma_file));
    }

    if($emake_autodepend ne '') {
        push(@args, '--emake-autodepend=' . $emake_autodepend);
    }

    if($emake_optimize_deps ne '') {
        push(@args, '--emake-optimize-deps=' . $emake_optimize_deps);
    }

    if($emake_optimize_schedule ne '') {
        push(@args, '--emake-optimize-schedule=' . $emake_optimize_schedule);
    }

    if($emake_parse_avoidance ne '') {
        push(@args, '--emake-parse-avoidance=' . $emake_parse_avoidance);
    }

    if($emake_history_force ne '') {
        push(@args, '--emake-history-force=' . $emake_history_force);
    }

    #if($emake_readdir_conflicts ne '') {
    #    push(@args, '--emake-readdir-conflicts=' . $emake_readdir_conflicts);
    #}

    if($emake_asset_dir && $emake_asset_dir ne '') {
		$emake_asset_dir = sortPathSep($emake_asset_dir);

		if($emake_use_sharedcache eq '0') {
			if(createDirectoryIfNotExists($emake_asset_dir, '0777') == 0) {
				print "Couldn't create asset directory : $emake_asset_dir\n";
				exit 1; 
			}

			push(@args, '--emake-assetdir=' . $emake_asset_dir);		
		} else {
			if(createDirectoryIfNotExists($emake_asset_dir_copy, '0777') == 0) {
				print "Couldn't create local asset directory : $emake_asset_dir_copy\n";
				exit 1; 
			}

			push(@args, '--emake-shared-assetdir=' . $emake_asset_dir);		
			push(@args, '--emake-assetdir=' . $emake_asset_dir_copy);		
		}
    }

    if($emake_jobcache && $emake_jobcache ne '') {
        push(@args, '--emake-jobcache=' . $emake_jobcache);
    }

    if($emake_history && $emake_history ne '') {
        push(@args, '--emake-history=' . $emake_history);
    }

	if($emake_use_learning_build eq '0'){
		#if(! -e $emake_historyfile && ! -e $emake_history_copy_file) {
		#	print "There is no learning build data yet. Please run learning build first..\n";
		#	exit 1;
		#}

		if(! -e $emake_history_copy_file){
			#my %params = ();
			#$params{"sourceFile"} = $emake_historyfile;
			#$params{"destinationFile"} = $emake_history_copy_file; 
			#CHECK_EF_FUNC_ERROR($ec, "createJobStep", "CO_CONF_EA", "CO_File_Copy", "History File Copy", \%params); 
			push(@args, '--emake-maxagents=10');
			push(@args, '--emake-readdir-conflicts=1');
		} else {
			push(@args, '--emake-readdir-conflicts=0');
		}

		$emake_historyfile = $emake_history_copy_file; 
	}

	if($emake_historyfile && $emake_historyfile ne '') {
        $emake_historyfile = sortPathSep($emake_historyfile);

		my $historyPath  = dirname($emake_historyfile);
		if(createDirectoryIfNotExists($historyPath, '0777') == 0) {
			print "Couldn't create history directory : $historyPath";
			exit 1; 
		}

		push(@args, '--emake-historyfile=' . $emake_historyfile);
	}

    push(@args, '--emake-suppress-include=*.d');
    push(@args, '--emake-suppress-include=*.P');

    if($emake_parse_avoidance_ignore_env  && $emake_parse_avoidance_ignore_env  ne '') {
		$emake_parse_avoidance_ignore_env =~ s/^\s+|\s+$//g;
		my @emake_parse_avoidance_ignore_env_arr = split("\n",$emake_parse_avoidance_ignore_env);
        foreach my $emakeCommand (@emake_parse_avoidance_ignore_env_arr) {
			 push(@args, '--emake-parse-avoidance-ignore-env=' . "$emakeCommand");
        }
    }

    if($eamke_additional_options  && $eamke_additional_options  ne '') {
		$eamke_additional_options =~ s/^\s+|\s+$//g;
		my @eamke_additional_option_arr = split("\n",$eamke_additional_options);
        foreach my $emakeCommand (@eamke_additional_option_arr) {
            push(@args, "$emakeCommand");
        }
    }

	my $result = join(" \\\n", @args);

	print "EA OPTIONS : \n";
	print "$result\n";

	CHECK_EF_FUNC_ERROR($ec, "setProperty", "/myJob/$targetProcName/EA/emakeCommandLine", $result); 
}


my $ec = new ElectricCommander();  
my $emake_cm = '$[--emake-cm]';
my $emake_root = '$[--emake-root]';
my $emake_class = '$[--emake-class]';
my $emake_autodepend = '$[--emake-autodepend]';
my $emake_optimize_deps = '$[--emake-optimize-deps]';
my $emake_optimize_schedule = '$[--emake-optimize-schedule]';
my $emake_parse_avoidance = '$[--emake-parse-avoidance]';
my $emake_parse_avoidance_ignore_env = '$[--emake-parse-avoidance-ignore-env]';
my $emake_readdir_conflicts = '$[--emake-readdir-conflicts]'; 
my $emake_use_jobcache = '$[--emake-use-jobcache]'; 
my $emake_jobcache = '$[--emake-jobcache]'; 
my $emake_use_sharedcache = '$[--emake-use-shared-jobcache]'; 
my $emake_asset_dir = '$[--emake-shared-assetdir]';
my $emake_build_label = '$[/myJob/jobName]';
my $emake_pragma_file = '$[--emake-pragmafile]';
my $eamke_suppress_include = '$[--emake-suppress-include]';
my $eamke_additional_options = '$[--emake-additional-options]';
my $emake_history_force = '$[--emake-history-force]';
my $emake_use_learning_build = '$[--emake-use-learning-build]';
my $emake_history = '$[--emake-history]';
my $emake_historyfile = '$[--emake-historyfile]';
my $emake_use_shared_history = '$[--emake-use-shared-history]';
my $branchName = '$[BRANCH_NAME]';

createEmakeOptions(
$ec,
$emake_cm, 
$emake_root, 
$emake_class,
$emake_use_learning_build,
$emake_build_label, 
$emake_pragma_file,
$emake_autodepend, 
$emake_optimize_deps, 
$emake_optimize_schedule,
$emake_history_force,
$emake_parse_avoidance,
$emake_parse_avoidance_ignore_env,
$eamke_suppress_include,
