sub createEmakeOptions {
	my $ec = shift;
	my $emake_cm = shift;
	my $emake_root =  shift;
	my $emake_class =  shift;
	my $emake_use_learning_build = shift;
	my $emake_build_label_base = shift;
	my $emake_autodepend =  shift;
	my $emake_optimize_deps =  shift;
	my $emake_optimize_schedule =  shift; 
	my $emake_parse_avoidance =  shift;
	my $emake_parse_avoidance_ignore_env = shift;  
	my $emake_history =  shift; 
	my $tag_name =  shift; 

    my @args = ();
	my $assetBaseDir = "";
	my $sharedAssetBaseDir = "";

    if($emake_cm && $emake_cm ne '') {
        push(@args, '--emake-cm=' . $emake_cm);
    }

    if($emake_root && $emake_root ne '') {
		$emake_root = sortPathSep($emake_root); 
    }

    if($emake_class && $emake_class ne '') {
        push(@args, '--emake-class=' . $emake_class);
    }

	push(@args, '--emake-jobcache=gcc');
	push(@args, '--emake-history-force=0');
	push(@args, '--emake-emulation=gmake4.1');

    if($emake_autodepend && "$emake_autodepend" eq "1") {
        push(@args, '--emake-autodepend=1');
    }

    if($emake_optimize_deps && "$emake_optimize_deps" eq "1") {
        push(@args, '--emake-optimize-deps=1');
    }

    if($emake_optimize_schedule && "$emake_optimize_schedule" eq "1") {
        push(@args, '--emake-optimize-schedule=1');
    }

    if($emake_parse_avoidance && "$emake_parse_avoidance" eq "1") {
        push(@args, '--emake-parse-avoidance=1');
    }

	my $emake_asset_base_dir = "";
	my $emake_shared_asset_base_dir = "";
    if($emake_use_learning_build && $emake_use_learning_build ne '') {
        push(@args, '--emake-maxagents=8');
		push(@args, '--emake-readdir-conflicts=1');
		$emake_asset_base_dir = "$[/myProject/EA/EA_DATA_ROOT]/$[/myJob/projectName]";	

    } else {
		$emake_shared_asset_base_dir = "$[/myProject/EA/EA_DATA_ROOT]/$[/myJob/projectName]";	
		$emake_asset_base_dir = "$[/myProject/EA/EA_DATA_LOCAL_ROOT]/$[/myJob/projectName]/$[/myJob/procedureName]";	
		if($tag_name && "$tag_name" ne "") {
			$emake_asset_base_dir = "$[/myProject/EA/EA_DATA_LOCAL_ROOT]/$[/myJob/projectName]/$[/myJob/procedureName]_$tag_name";
		}

		if(!checkExistFileOrDir($emake_shared_asset_base_dir)) {
			$emake_shared_asset_base_dir = "";
		}

		if(!checkExistFileOrDir($emake_asset_base_dir)) {
			#push(@args, '--emake-maxagents=10');
			#push(@args, '--emake-readdir-conflicts=1');
            push(@args, '--emake-readdir-conflicts=0');
		} else {
			push(@args, '--emake-readdir-conflicts=0');
		}
	}

	$emake_asset_base_dir = sortPathSep($emake_asset_base_dir);
	if(createDirectoryIfNotExists($emake_asset_base_dir, '0777') == 0) {
		print "Couldn't create asset directory : $emake_asset_base_dir";
		exit 1; 
	}

	$assetBaseDir = $emake_asset_base_dir;	
	$sharedAssetBaseDir = $emake_shared_asset_base_dir;

	my $emake_pragma_file = "$[/myProject/EA/EA_CODE_ROOT]/$[/myJob/projectName]/emake.pragma";
    push(@args, '--emake-pragmafile=' . sortPathSep($emake_pragma_file));

    if($emake_history && $emake_history ne '') {
        push(@args, '--emake-history=' . $emake_history);
    }

    if($emake_parse_avoidance && "$emake_parse_avoidance" eq "1" && $emake_parse_avoidance_ignore_env  && $emake_parse_avoidance_ignore_env  ne '') {
		$emake_parse_avoidance_ignore_env =~ s/^\s+|\s+$//g;
		my @emake_parse_avoidance_ignore_env_arr = split("\n",$emake_parse_avoidance_ignore_env);
        foreach my $emakeCommand (@emake_parse_avoidance_ignore_env_arr) {
			 push(@args, '--emake-parse-avoidance-ignore-env=' . "$emakeCommand");
        }
    }

	my $eaCMD = join(" \\\n", @args);

	print "EA Base Build Label : \n";
	print "$emake_build_label_base\n\n";

	print "EA Shared Asset Directory : \n";
	print "$sharedAssetBaseDir\n\n";	

	print "EA Base Asset Directory : \n";
	print "$assetBaseDir\n\n";

	print "EA OPTIONS : \n";
	print "$eaCMD\n";

	$eaCMD = join(" ", @args);

	CHECK_EF_FUNC_ERROR($ec, "setProperty", "/myJob/EA/emakeBaseBuildLabel", $emake_build_label_base); 
	CHECK_EF_FUNC_ERROR($ec, "setProperty", "/myJob/EA/emakeBaseAssetDir", $assetBaseDir); 
	CHECK_EF_FUNC_ERROR($ec, "setProperty", "/myJob/EA/emakeSharedAssetBaseDir", $sharedAssetBaseDir);
	CHECK_EF_FUNC_ERROR($ec, "setProperty", "/myJob/EA/emakeCommandLine", $eaCMD); 
	CHECK_EF_FUNC_ERROR($ec, "setProperty", "/myJob/EA/emakeRoot", $emake_root); 
	CHECK_EF_FUNC_ERROR($ec, "setProperty", "/myJob/EA/emakeDataRoot","$[/myProject/EA/EA_CODE_ROOT]/$[/myJob/projectName]"); 
}


