if(checkExistFileOrDir($srcFile) ) {
	$params{"sourceFile"} = $srcFile;
	$params{"destinationFile"} = "$emakeBaseAssetDir/bemake_package_blacklist"; 
	CHECK_EF_FUNC_ERROR($ec, "createJobStep", "CO_CONF_EA", "CO_File_Copy", 'File Copy - bemake_package_blacklist', \%params); 
} else {
	$srcFile = "$emakeDataRoot/bemake_package_blacklist";
	if(checkExistFileOrDir($srcFile) ) {
		$params{"sourceFile"} = $srcFile;
		$params{"destinationFile"} = "$emakeBaseAssetDir/bemake_package_blacklist"; 
		CHECK_EF_FUNC_ERROR($ec, "createJobStep", "CO_CONF_EA", "CO_File_Copy", 'File Copy - bemake_package_blacklist', \%params); 
