sub checkEmakeOptions { 
	my $emake_cm = shift;
	my $emake_root =  shift;
	my $emake_class =  shift;

	if($emake_cm eq ""){
		print "Please put Cluster Manager Host.\n";
		exit 1;
	}

	if($emake_root eq "" || checkExistFileOrDir($emake_root) == 0){
		print "Please put EMAKE ROOT which already exists.\n";
		exit 1;
	}

	if($emake_class eq ""){
		print "Please put Emake Build Class which already registerd in cluster manager.\n";
		exit 1;
	}
}


