my $ec = new ElectricCommander();

my $emake_root = '$[--emake-root]';
my $targetProjName = '$[/myParent/parent/parent/parent/projectName]';
my $targetProcName = '$[/myParent/parent/parent/parent/procedureName]';


my %params = ();

my $emakeBaseAssetDir = CHECK_EF_FUNC_ERROR($ec, "getPropertyStringValue", "/myJob/$targetProcName/EA/emakeBaseAssetDir");
my $emakeDataRoot = CHECK_EF_FUNC_ERROR($ec, "getPropertyStringValue", "/myJob/$targetProcName/EA/emakeDataRoot"); 

print $emakeBaseAssetDir ."\n";
print $emakeDataRoot ."\n";

#BLACK LIST COPY
my $srcFile = "$emakeDataRoot/$targetProcName/bemake_package_blacklist";
if(checkExistFileOrDir($srcFile) ) {
	$params{"sourceFile"} = $srcFile;
	$params{"destinationFile"} = "$emakeBaseAssetDir/bemake_package_blacklist"; 
	CHECK_EF_FUNC_ERROR($ec, "createJobStep", "CO_CONF_EA", "CO_File_Copy", 'File Copy - bemake_package_blacklist', \%params); 
} else {
	$srcFile = "$emakeDataRoot/bemake_package_blacklist";
	if(checkExistFileOrDir($srcFile) ) {
		$params{"sourceFile"} = $srcFile;
		$params{"destinationFile"} = "$emakeBaseAssetDir/bemake_package_blacklist"; 
		CHECK_EF_FUNC_ERROR($ec, "createJobStep", "CO_CONF_EA", "CO_File_Copy", 'File Copy - bemake_package_blacklist', \%params); 
	} 
}

#WHITE LIST COPY

$srcFile = "$emakeDataRoot/$targetProcName/bemake_package_whitelist";
if(checkExistFileOrDir($srcFile) ) {
	$params{"sourceFile"} = $srcFile;
	$params{"destinationFile"} = "$emakeBaseAssetDir/bemake_package_whitelist"; 
	CHECK_EF_FUNC_ERROR($ec, "createJobStep", "CO_CONF_EA", "CO_File_Copy", 'File Copy - bemake_package_whitelist', \%params); 
} else {
	$srcFile = "$emakeDataRoot/bemake_package_whitelist";
	if(checkExistFileOrDir($srcFile) ) {
		$params{"sourceFile"} = $srcFile;
		$params{"destinationFile"} = "$emakeBaseAssetDir/bemake_package_whitelist"; 
		CHECK_EF_FUNC_ERROR($ec, "createJobStep", "CO_CONF_EA", "CO_File_Copy", 'File Copy - bemake_package_whitelist', \%params); 
	}
}

