$emake_pragma_file,
$emake_autodepend, 
$emake_optimize_deps, 
$emake_optimize_schedule,
$emake_history_force,
$emake_parse_avoidance,
$emake_parse_avoidance_ignore_env,
$eamke_suppress_include,
$emake_readdir_conflicts,
$emake_use_sharedcache,
$emake_jobcache,
$emake_asset_dir,
$emake_history,
$emake_historyfile,
$emake_use_shared_history,
$eamke_additional_options);

