sub createEmakeOptions {
	my $ec = shift;
	my $emake_cm = shift;
	my $emake_root =  shift;
	my $emake_class =  shift;
	my $emake_use_learning_build = shift;
	my $emake_build_label = shift;
	my $emake_pragma_file = shift;
	my $emake_autodepend =  shift;
	my $emake_optimize_deps =  shift;
	my $emake_optimize_schedule =  shift;
	my $emake_history_force = shift;
	my $emake_parse_avoidance =  shift;
	my $emake_parse_avoidance_ignore_env = shift;
	my $eamke_suppress_include = shift;
	my $emake_readdir_conflicts =  shift; 
	my $emake_use_sharedcache = shift;
	my $emake_jobcache =  shift; 
	my $emake_asset_dir =  shift; 
	my $emake_history =  shift; 
	my $emake_historyfile =  shift;
	my $emake_use_shared_history = shift;
	my $eamke_additional_options = shift;
	my $emake_history_copy_file= "$[/myProject/EA/EA_DATA_LOCAL_ROOT]/HISTORY/$[/myJob/projectName]_$[/myJob/procedureName].data";
    my $emake_asset_dir_copy = "$[/myProject/EA/EA_DATA_LOCAL_ROOT]/ASSET/$[/myJob/projectName]_$[/myJob/procedureName]";

    my @args = ();

    if($emake_cm && $emake_cm ne '') {
        push(@args, '--emake-cm=' . $emake_cm);
    }

    if($emake_root && $emake_root ne '') {
		my $annofile = $emake_root.'/../logs/@ECLOUD_BUILD_ID@.anno';
		my $logfile = $emake_root.'/../logs/@ECLOUD_BUILD_ID@.dlog';

		$emake_root = sortPathSep($emake_root);
		$annofile = sortPathSep($annofile);
		$logfile = sortPathSep($logfile);

		push(@args, '--emake-root='. $emake_root);
		push(@args, '--emake-annofile='. $annofile);
		push(@args, '--emake-logfile='. $logfile);
    }

    if($emake_class && $emake_class ne '') {
        push(@args, '--emake-class=' . $emake_class);
    }

    push(@args, '--emake-blind-create=1');


    if($emake_use_learning_build && $emake_use_learning_build ne '') {
        push(@args, '--emake-maxagents=1');
    }

    if($emake_build_label && $emake_build_label ne '') {
        push(@args, '--emake-build-label=' . $emake_build_label);
    }

    if($emake_pragma_file && $emake_pragma_file ne '') {
        push(@args, '--emake-pragmafile=' . sortPathSep($emake_pragma_file));
    }

    if($emake_autodepend ne '') {
        push(@args, '--emake-autodepend=' . $emake_autodepend);
    }

    if($emake_optimize_deps ne '') {
        push(@args, '--emake-optimize-deps=' . $emake_optimize_deps);
    }

    if($emake_optimize_schedule ne '') {
        push(@args, '--emake-optimize-schedule=' . $emake_optimize_schedule);
    }

    if($emake_parse_avoidance ne '') {
        push(@args, '--emake-parse-avoidance=' . $emake_parse_avoidance);
    }

    if($emake_history_force ne '') {
        push(@args, '--emake-history-force=' . $emake_history_force);
    }

    #if($emake_readdir_conflicts ne '') {
    #    push(@args, '--emake-readdir-conflicts=' . $emake_readdir_conflicts);
    #}

    if($emake_asset_dir && $emake_asset_dir ne '') {
		$emake_asset_dir = sortPathSep($emake_asset_dir);

		if($emake_use_sharedcache eq '0') {
			if(createDirectoryIfNotExists($emake_asset_dir, '0755') == 0) {
				print "Couldn't create asset directory : $emake_asset_dir";
				exit 1; 
			}

			push(@args, '--emake-assetdir=' . $emake_asset_dir);		
		} else {
			if(createDirectoryIfNotExists($emake_asset_dir_copy, '0755') == 0) {
				print "Couldn't create local asset directory : $emake_asset_dir_copy";
				exit 1; 
			}

			push(@args, '--emake-shared-assetdir=' . $emake_asset_dir);		
			push(@args, '--emake-assetdir=' . $emake_asset_dir_copy);		
		}
    }

    if($emake_jobcache && $emake_jobcache ne '') {
        push(@args, '--emake-jobcache=' . $emake_jobcache);
    }

    if($emake_history && $emake_history ne '') {
        push(@args, '--emake-history=' . $emake_history);
    }

	if($emake_use_learning_build eq '0'){
		#if(! -e $emake_historyfile && ! -e $emake_history_copy_file) {
		#	print "There is no learning build data yet. Please run learning build first..\n";
		#	exit 1;
		#}

		if(! -e $emake_history_copy_file){
			#my %params = ();
			#$params{"sourceFile"} = $emake_historyfile;
			#$params{"destinationFile"} = $emake_history_copy_file; 
			#CHECK_EF_FUNC_ERROR($ec, "createJobStep", "CO_CONF_EA", "CO_File_Copy", "History File Copy", \%params); 
			push(@args, '--emake-maxagents=8');
			push(@args, '--emake-readdir-conflicts=1');
		} else {
			push(@args, '--emake-readdir-conflicts=0');
		}

		$emake_historyfile = $emake_history_copy_file; 
	}

	if($emake_historyfile && $emake_historyfile ne '') {
        $emake_historyfile = sortPathSep($emake_historyfile);

		my $historyPath  = dirname($emake_historyfile);
		if(createDirectoryIfNotExists($historyPath, '0755') == 0) {
			print "Couldn't create history directory : $historyPath";
			exit 1; 
		}

		push(@args, '--emake-historyfile=' . $emake_historyfile);
	}

    push(@args, '--emake-suppress-include=*.d');
    push(@args, '--emake-suppress-include=*.P');

    if($emake_parse_avoidance_ignore_env  && $emake_parse_avoidance_ignore_env  ne '') {
		$emake_parse_avoidance_ignore_env =~ s/^\s+|\s+$//g;
		my @emake_parse_avoidance_ignore_env_arr = split("\n",$emake_parse_avoidance_ignore_env);
        foreach my $emakeCommand (@emake_parse_avoidance_ignore_env_arr) {
			 push(@args, '--emake-parse-avoidance-ignore-env=' . "$emakeCommand");
        }
    }

    if($eamke_additional_options  && $eamke_additional_options  ne '') {
		$eamke_additional_options =~ s/^\s+|\s+$//g;
		my @eamke_additional_option_arr = split("\n",$eamke_additional_options);
        foreach my $emakeCommand (@eamke_additional_option_arr) {
            push(@args, "$emakeCommand");
        }
    }

	my $result = join(" \\\n", @args);

	print "EA OPTIONS : \n";
	print "$result\n";

	CHECK_EF_FUNC_ERROR($ec, "setProperty", "/myJob/EA/emakeCommandLine", $result); 
}


