use ElectricCommander;
use Archive::Zip;
use Digest::MD5;
use Digest::SHA;
use File::Basename;
use File::Copy::Recursive qw(rcopy pathempty);
use File::Path;
use File::Spec;
use File::Glob 'bsd_glob';
use strict;
use Cwd qw(cwd);

sub copyFile {
    my ($srcFile, $destFile, $replaceIfExists) = @_;

    if ($srcFile =~ /["']/) {
        die("Source file name $srcFile contains an illegal quote character\n");
    }

    if ($destFile =~ /["']/) {
        die("Destination file name $destFile contains an illegal quote " .
            "character\n");
    }

    if (!$replaceIfExists && -e $destFile && ! -d $destFile) {
        die("Destination file $destFile already exists.\n");
    }

    if ($replaceIfExists && -d $destFile) {
        pathempty($destFile) 
            or die("Couldn't clear $destFile prior to copy: $!\n");
    }

    my @parts = File::Spec->splitdir($srcFile);
    if ($parts[$#parts] =~ /\*/) {
        if (-e $destFile) {
            if (! -d $destFile) {
                die("Destination file $destFile already exists and is not " .
                    "a directory.\n");
            }
        } else {
            eval {
                mkpath $destFile;
            };
            die("Couldn't create destination directory: $@\n") if $@;
        }
    } else {
        die("Source file $srcFile does not exist.\n") if (! -e $srcFile);
    }

    $File::Copy::Recursive::CPRFComp = 1;

    for my $path (bsd_glob($srcFile)) {
        rcopy($path, $destFile) or die  "Copy failed: $!\n";
    }

    return;
}

my $ec = new ElectricCommander();

my $dir = cwd;
print "$dir\n";

my $srcFile = <<'EOF';
$[sourceFile]
EOF

my $destFile = <<'EOF';
$[destinationFile]
EOF

chomp($srcFile);
chomp($destFile);

copyFile($srcFile, $destFile, 1);

print "Successfully " .  "copied" . " \"$srcFile\" to \"$destFile\".\n";

