my $newtimeout = 600000;
my $ec = new ElectricCommander();

# Set the time out to newtimeout so the ec commands won't time out at 3 mins
$ec->setTimeout($newtimeout);

my $procedurePatternList = '$[ProcedurePattern_List]';

$procedurePatternList=~s/\r//gs;# remove all \r tab
$procedurePatternList=~s/\t//gs;# remove all \t
$procedurePatternList=~s/, /,/gs;# remove space

my @procedureArray = split(/\n/, $procedurePatternList);
my $procedureHashRef;
%{$procedureHashRef} = map { my ( $key, $value ) = split ","; ( $key, $value ) } @procedureArray;
#print '[Result] : '. Dumper \%{$procedureHashRef};

my $count = 0;
my $total = 0;

my $ea_paramName = "BUILD_ACCELERATION";
my $paramName = "System_OS_1_TARGET";
my $plm_paramName = "PLM_SW_DEPLOY";

foreach my $projectName ( sort keys %{$procedureHashRef} ){
	my $procedurePattern = $procedureHashRef->{$projectName};
    my @procList;
    my $project = $ec->getProcedures("$projectName");
    my $nodeset = $project->findnodes("//procedure");

 #   print"Matched                                                          Unmatched\n";

    push (@procList, $_->findvalue("procedureName")) foreach ($nodeset->get_nodelist);

    foreach my $eachProcedureName(@procList){
    $total++;
      if($eachProcedureName =~ m/$procedurePattern/){
            if(checkFormalParamExist($ec, $paramName, $projectName, $eachProcedureName)){

               if(checkFormalParamExist($ec, $ea_paramName, $projectName, $eachProcedureName)) {

                  print"$eachProcedureName ";
                  print"    ***********$ea_paramName default value \n";

                 CHECK_EF_FUNC_ERROR($ec, "modifyProcedureFormalParamDefaultValue", $ea_paramName, $projectName, $eachProcedureName, 1);
               }
               else
               {
                  print "$eachProcedureName ";
                  print"    ----------$ea_paramName is not exist \n";

                  CHECK_EF_FUNC_ERROR($ec, "createCheckBoxParam", $ea_paramName, $projectName, $eachProcedureName, '', 0, '1');               
               }
               $count++
            }
	   }
#       else
#       {
#           print"                                                                  $projectName, $eachProcedureName\n";
#       
#       }
	}
}

print "count: $count\n";
print "total: $total\n";

